import React from "react"
import ContactList from "./ContactList/ContactList";
import Filter from "./Filter/Filter";
import Phonebook from "./Phonebook/Phonebook";


class App extends React.Component{

  state = {
    contacts: [{id: 'id-1', name: 'Rosie Simpson', number: '459-12-56'},
    {id: 'id-2', name: 'Hermione Kline', number: '443-89-12'},
    {id: 'id-3', name: 'Eden Clements', number: '645-17-79'},
    { id: 'id-4', name: 'Annie Copeland', number: '227-91-26' },],
    filter: '',
  }

  onFilterChange = e => {
    const { name, value } = e.target;

    this.setState({ [name]: value })
  };
  
  addContact = data => {
    const contactName =
      this.state.contacts.map(contact =>
      contact.name.toLowerCase())
    if (!contactName.includes(data.name.toLowerCase())) {
      this.setState(({ contacts }) => ({
      contacts: [data, ...contacts],
      }))
    }
    else {
      return alert(`${data.name} is allready in contacts`)
    } 
    
  };

  filter = () => {
    const { contacts, filter } = this.state;
    const normalizedFilter = filter.toLowerCase();
    return contacts.filter(contact =>
      contact.name.toLowerCase().includes(normalizedFilter)
    );
  };
  
  onDelete = id => {
    // const contactId = this.state.contacts.map(contact => contact.id)
    // if (contactId.includes(id)) {
    //   this.setState(prevState => ({
    //     contacts: prevState.contacts.filter(contact => contact.id === contactId),
    //   }));
    // }
  }

  render() {
    const filter = this.filter();

    return (
      <div>
        <h1>Phonebook</h1>
        <Phonebook addContact={this.addContact} />
        <h2>Contacts</h2>
        <Filter onChange={this.onFilterChange} />
        <ContactList filter={filter} onDelete={this.onDelete} />
      </div>
    )
  }
}

export default App
