import React from "react"
import shortId from "shortid"

class App extends React.Component{

  state = {
    contacts: [{id: 'id-1', name: 'Rosie Simpson', number: '459-12-56'},
    {id: 'id-2', name: 'Hermione Kline', number: '443-89-12'},
    {id: 'id-3', name: 'Eden Clements', number: '645-17-79'},
    { id: 'id-4', name: 'Annie Copeland', number: '227-91-26' },],
    filter: '',
    name: '',
    number: '',
  }

  nameInputId = shortId.generate();
  numberInputId = shortId.generate();
  filterInputId = shortId.generate();
  

  onInputChange = e => {

    const { name, value } = e.target;

    this.setState({ [name]: value })  
  }

  addContact = () => {
    const contact = {
      id: shortId.generate(),
      name: this.state.name,
      number: this.state.number,
    }
    
   this.setState(({ contacts }) => ({
        contacts: [contact, ...contacts],
    }))
  };

  formReset = () => {
    this.setState(
      {
        name: '',
        number: '',
      }
    )
  };

  // contactItemAdd = () => {
  //   return this.state.contacts.map(contact => (<li key={contact.id}>{contact.name} {contact.number}</li>))
  // }

  filter = () => {
    const { contacts, filter } = this.state;
    const normalizedFilter = filter.toLowerCase();
    return contacts.filter(contact =>
      contact.name.toLowerCase().includes(normalizedFilter)
    );
  };

  onSubmitHandle = e => {
  e.preventDefault();
        
    this.addContact()

    this.formReset()

  };
  
    render() {
      return (
        <div>

        <h1>Phonebook</h1> 

        <form onSubmit={this.onSubmitHandle}>
          <label htmlFor={this.nameInputId}>
            Name
              <input
                type="text"
                name="name"
                pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
                title="Name may contain only letters, apostrophe, dash and spaces. For example Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan"
                required
                value={this.state.name}
                onChange={this.onInputChange}
                id={this.nameInputId}
              />
          </label>
            <br />
            <label htmlFor={this.numberInputId}>
              Number
              <input
                type="tel"
                name="number"
                pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
                title="Phone number must be digits and can contain spaces, dashes, parentheses and can start with +"
                required
                value={this.state.number}
                id={this.numberInputId}
                onChange={this.onInputChange}
            />
            </label>
              <br />
              <button type = "submit">Add contact</button>
          
          </form>
          <h2>Contacts</h2>
          <label htmlFor={this.filterInputId}>
            Find contacts by name <br />
            <input type="text" name="filter" id={this.filterInputId} onChange={this.onInputChange} />
          </label>
          <ul>{ this.filter.map(contact => (<li key={contact.id}>{contact.name} {contact.number}</li> ))}</ul>
        </div>
      )
    }
}

export default App
