import React from "react"
import shortId from "shortid"
import Phonebook from "./Phonebook/Phonebook";


class App extends React.Component{

  state = {
    contacts: [{id: 'id-1', name: 'Rosie Simpson', number: '459-12-56'},
    {id: 'id-2', name: 'Hermione Kline', number: '443-89-12'},
    {id: 'id-3', name: 'Eden Clements', number: '645-17-79'},
    { id: 'id-4', name: 'Annie Copeland', number: '227-91-26' },],
    filter: '',
  }

 
  filterInputId = shortId.generate();
  

  // onInputChange = e => {

  //   const { name, value } = e.target;

  //   this.setState({ [name]: value })  
  // }

  addContact = () => {
    const contact = {
      id: shortId.generate(),
      name: this.state.name,
      number: this.state.number,
    }
    
   this.setState(({ contacts }) => ({
        contacts: [contact, ...contacts],
    }))
  };

  formReset = () => {
    this.setState(
      {
        name: '',
        number: '',
      }
    )
  };

  filter = () => {
    const { contacts, filter } = this.state;
    const normalizedFilter = filter.toLowerCase();
    return contacts.filter(contact =>
      contact.name.toLowerCase().includes(normalizedFilter)
    );
  };

  onSubmitHandle = e => {
  e.preventDefault();
        
    this.addContact()

    this.formReset()

  };
  
  render() {
    const filter = this.filter();

    return (
      <div>

      <Phonebook />
        
          <h2>Contacts</h2>
          <label htmlFor={this.filterInputId}>
            Find contacts by name <br />
            <input type="text" name="filter" id={this.filterInputId} onChange={this.onInputChange} />
          </label>
          <ul>{ filter.map(contact => (<li key={contact.id}>{contact.name} {contact.number}</li>)) }</ul>
      </div>
    )
  }
}

export default App
