import {useState, useEfect} from "react"
import ContactList from "./ContactList/ContactList";
import Filter from "./Filter/Filter";
import Phonebook from "./Phonebook/Phonebook";


const App = () =>{

  const [contacts, setContacts] = useState(() =>{
    return JSON.parse(window.localStorage.getItem("contacts")) ?? contacts.length !== 0});
  
  const [filter, setFilter] = useState("");

useEfect(localStorage.setItem("contacts", JSON.stringify(contacts)), [contacts])

  const addContact = data => {
    const contactName =
      contacts.map(contact =>
      contact.name.toLowerCase())
    if (!contactName.includes(data.name.toLowerCase())) {
      setContacts([data, ...contacts])
      }
    
    else {
      return alert(`${data.name} is allready in contacts`)
    } 
    
  };

  const filterCheck = () => {
    const normalizedFilter = filter.toLowerCase();
    return contacts.filter(contact =>
      contact.name.toLowerCase().includes(normalizedFilter)
    );
  }

  const onDelete = id => {
    setContacts(contacts.filter(contact => contact.id !== id))
    }
  

return (
      <div>
        <h1>Phonebook</h1>
        <Phonebook addContact={addContact} />
        <h2>Contacts</h2>
        <Filter onChange={e => setFilter(e.target.value)} filter={filter} />
        <ContactList filter={filterCheck()} onDelete={onDelete} />
      </div>
    )

  
  // componentDidUpdate(prevProps, prevState) {
  //   if (this.state.contacts !== prevState.contacts) {
  //   localStorage.setItem("contacts", JSON.stringify(this.state.contacts))
  // }
  // };

